mapper.select:
SELECT USER_ID
FROM USER
WHERE USER_NAME = #{ userName }
AND USER_EMAIL = #{ userEmail }

SELECT USER_PW
       ,USER_ID
FROM USER
WHERE USER_ID = #{ userId }
AND USER_NAME = #{ userName }
AND USER_EMAIL = #{ userEmail }

mapper.update:
UPDATE USER
   SET USER_PW = #{ userPw }
 WHERE USER_ID = #{ userId }

mapper.select:
SELECT COUNT(USER_ID)
  FROM USER
 WHERE USER_ID = #{ userId }

SELECT COUNT(USER_NAME)
FROM USER
WHERE USER_NAME = #{ userName }

SELECT COUNT(USER_EMAIL)
FROM USER
WHERE USER_EMAIL = #{ userEmail }

mapper.insert:
INSERT INTO USER(
                 USER_ID
                ,USER_NAME
                ,USER_PW
                ,USER_PHONE
                ,USER_ADD
                ,USER_EMAIL
                )
VALUES (
        #{ userId }
        ,#{ userName }
        ,#{ userPw }
        ,#{ userPhone }
        ,#{ userAdd }
        ,#{ userEmail }
)

mapper.update:
UPDATE  USER
   SET  USER_PHONE = #{ userPhone }
        ,USER_ADD = #{ userAdd }
        ,USER_EMAIL = #{ userEmail }
 WHERE USER_ID = #{ userId }
